package myanswers;

import org.junit.Test;

public class CharacterReplacement {

    public int characterReplacement(String s, int k) {

        int count;
        int max = 0;
        for (int i = 0; i < s.length(); i++) {
            // get the character
            char ch ;

            for (int a = 0; a < 26; a++) {
                ch = (char) ('A' + a);
                count = k;
                int j = i;
                for (; j < s.length(); j++) {
                    if (s.charAt(j) != ch && count == 0) {
                        break;
                    }
                    if (s.charAt(j) != ch) {
                        count--;
                    }
                }
                max = Math.max(j - i, max);
            }
        }
        return max;
    }



    public int characterReplacement2(String s, int k) {
        int len = s.length();
        int[] count = new int[26];
        int start = 0, maxCount = 0, maxLength = 0;
        for (int end = 0; end < len; end++) {

            maxCount = Math.max(maxCount, ++count[s.charAt(end) - 'A']);
            // if we are in process of finding new maxcount, then this if condition will remain false
            // because we started then it was false, only way it can be true again is if we find
            // new maxcount greater than previos
            if(end - start + 1 - maxCount <= k) {
                // we are good, just expand
                maxLength = Math.max(maxLength, end - start + 1);
            }else {

                // if not
                // we need to find the new maxcount
                // shift the left window
                count[s.charAt(start) - 'A']--;
                start++;


                // this step is not needed as i mentioned in notes too
                maxCount = 0;
                for(int i = 0; i < 26; i++){
                    if(maxCount <  count[i]){
                        maxCount = count[i];
                    }
                }

            }
        }
        return maxLength;
    }

    @Test
    public void test() {
        String s = "";
        System.out.println(characterReplacement2(s, 7520));

    }
}
