package myanswers;

import org.junit.Test;

public class ValidIpAddress {

    public String validIPAddress(String queryIP) {

        if (isValidIpv4(queryIP)) {
            return "IPv4";
        }
        if (isValidIpV6(queryIP)) {
            return "IPv6";
        } else {
            return "Neither";
        }
    }

    boolean isValidIpv4(String s) {

        String[] tokens = s.split("\\.", -1);
        if (tokens.length != 4) {
            return false;
        }

        for (String token : tokens) {
            if (token.isEmpty()) {
                return false;
            }

            for (char c : token.toCharArray()) {
                if (!Character.isDigit(c)) {
                    return false;
                }
            }

            if (token.startsWith("0") && !token.equals("0")) {
                return false;
            }

            try {
                int val = Integer.parseInt(token);
                if (val > 255) {
                    return false;
                }
            }catch (NumberFormatException e){
                return false;
            }


        }
        return true;
    }

    boolean isValidIpV6(String s) {

        String[] tokens = s.split(":", -1);
        if (tokens.length != 8) {
            return false;
        }
        String hexdigits = "0123456789abcdefABCDEF";

        for (String token : tokens) {

            if (token.isEmpty() || token.length() > 4) {
                return false;
            }

            for (char c : token.toCharArray()) {

                if (hexdigits.indexOf(c)==-1) {
                    return false;
                }
            }
        }

        return true;
    }


    @Test
    public void test() {

        System.out.println(validIPAddress("..."));
    }
}
